// this file has no comments added

d3.json('./data.json').then( json => displayImages(json)); 

function displayImages(json){
    let app  = d3.select('#app').text('');
 
    let descending = (a,b) => b.date - a.date;
    let ascending  = (a,b) => a.date - b.date;

    let data = json.sort( (a,b)=>descending(a,b) );

    let card = app.selectAll('div.met-card')
                  .data(data)
                  .join('div')
                  .attr('class', 'met-card');

    card.append('div')
        .attr('class', 'image')
        .append('img')
        .attr('src', d => './images/' + d.filename);

    card.append('p')
        .attr('class', 'object-date')
        .text(d=>d.date);

    card.append('h2')
        .attr('class', 'title')
        .text(d=>d.title);
}